mapper.select:
select role_code
from sys_role
where role_id in (select role_id from sys_user_role where user_id = #{userId});

select distinct u.user_id, u.user_name, u.nick_name, u.email, u.phone_number, u.status, u.create_time from sys_user u left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where r.role_id = #{roleId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.phone_number like concat('%', #{phoneNumber}, '%')
